import HomeIcon from '@mui/icons-material/Home';
import StoreIcon from '@mui/icons-material/Store';
//import { Scrollbars } from "react-custom-scrollbars-2";
import { Scrollbars } from "react-custom-scrollbars-2";
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

import DeleteIcon from '@mui/icons-material/Delete';
import Alert from '@mui/material/Alert';
import AlertTitle from '@mui/material/AlertTitle';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import ShoppingBasketIcon from '@mui/icons-material/ShoppingBasket';
import "../stylesheet/order.css";
import PersonIcon from '@mui/icons-material/Person';
import { BoxContainer } from '../accountBox/common';
import Slide from '@mui/material/Slide';

import ContactSupportIcon from '@mui/icons-material/ContactSupport';

import PersonAddIcon from '@mui/icons-material/PersonAdd';
const theme = createTheme({
  palette: {
    secondary: {
      // This is green.A700 as hex.
      main: '#264653',
    },
  },
  typography: {
    h5: {
      fontWeight: 450 // or 'bold'
    },
    h4:{
      fontSize:'25px',
      fontWeight:450
    },
    h6:{
        fontSize:'18px'
    }
  }
});


function Userdetail() {
    const params = useParams();
  const Id=params.id;

  const {
    state: { cart },
    dispatch,
  } = CartState();
  
 
 
  const [user,setuser]=useState([]);
  const [order,setorder] = useState([]);
  const [rx,setrx] = useState([]);
  useEffect(() => {
    
    axios
    .get(`http://localhost:8000//user/${Id}/profile`)
    .then(res => {
console.log(res)
setrx(res.data.Rx)
setuser(res.data)
console.log(res.data);
axios
.get(`http://localhost:8000//user/${Id}/myorders`)
.then(ress => {
console.log(ress)
setorder(ress.data)
console.log(ress.data);

})
.catch(err => {
    console.log(err)
})
    })
    .catch(err => {
        console.log(err)
    })
        
}, [])
const [open,setOpen] = useState(false);
let history = useHistory();
const handleClose = () => {
  setOpen(false);
};
const deleteuser =async () =>{
  console.log(Id);
  const response= await fetch(`http://localhost:8000//user/${Id}/profile/delete`, {
      method:"DELETE"
  });
  
  setOpen(false);
  history.push("/logout");
};
const handleClickOpen = () => {
  setOpen(true);
 
};
console.log(user);
    return (
      <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1}} >
      <AppBar position="static" color="secondary">
      <Toolbar>
      
      <IconButton
        size="large"
        color="inherit"
      >
        
        <PersonAddIcon />
        
      </IconButton>
        <Typography variant="h5" component="div" sx={{ flexGrow: 1 }} >
        Profile
        </Typography>
        <Box>
        <Link to="/adminhome" className="link"> <HomeIcon fontSize="large" /> </Link>
        
        </Box>
        </Toolbar>
        </AppBar>
        <div id="newtb">
      
        <Card sx={{ maxWidth: 600,mt:7,pl:1,pr:1 }} elevation={6}>
        <CardMedia
        id="big"
        component="img"
        
        image="https://www.pngfind.com/pngs/m/470-4703547_icon-user-icon-hd-png-download.png"
        alt="green iguana"
        sx={{pl:22,pr:22}}
      />
      <CardMedia
      id="small"
      component="img"
      
      image="https://www.pngfind.com/pngs/m/470-4703547_icon-user-icon-hd-png-download.png"
      alt="green iguana"
      sx={{pl:10,pr:10}}
    />
        <CardContent>
          <Typography  gutterBottom variant="h4" component="div" >
          <div id="mar">  {user.name} </div>
          </Typography>
         
          <Typography variant="h6" color="text.secondary" align="left" sx={{mt:3}}>
           Mobile <span id="mo1">:</span>  {user.mobile}
          </Typography>
          
          <Typography variant="h6" color="text.secondary" align="left">
           Address   <span id="mo5">:</span> {user.address}
          </Typography>
          <Typography variant="h6" color="text.secondary" align="left">
          Pincode <span id="mo3">:</span> {user.pincode}
         </Typography>
         <Typography variant="h6" color="text.secondary" align="left">
          Orders <span id="mo4">:</span> {order.length}
         </Typography>
         <Typography variant="h6" color="text.secondary" align="left">
         Rx <span id="mo6">:</span> {rx.length}
        </Typography>
        </CardContent>
        
      </Card>
     
      </div>
      </Box>
      </ThemeProvider>
    );
  }

export default Userdetail
