import React,{useState,useEffect} from 'react';
import { Link } from 'react-router-dom';
import '../stylesheet/Counter.css'

import {useParams,useHistory} from 'react-router-dom';
import {Grid,Paper} from '@material-ui/core';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import { CartState } from "../context/Context";
import AccountCircle from '@mui/icons-material/AccountCircle';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import img from '../images/a1.jpg'
import img2 from '../images/a2.jpg'
import Dialog from '@material-ui/core/Dialog';
import FormControl, { useFormControl } from '@mui/material/FormControl';
import OutlinedInput from '@mui/material/OutlinedInput';
import FormHelperText from '@mui/material/FormHelperText';
import InputUnstyled from '@mui/core/InputUnstyled';
import SearchIcon from '@mui/icons-material/Search';
import InputBase from '@mui/material/InputBase';
import Divider from '@mui/material/Divider';
import DirectionsIcon from '@mui/icons-material/Directions';
import BallotIcon from '@mui/icons-material/Ballot';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { useTheme } from '@mui/material/styles';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import MuiAppBar from '@mui/material/AppBar';
import List from '@mui/material/List';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import { alpha } from '@mui/material/styles';
import Checkbox from '@mui/material/Checkbox';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormLabel from '@mui/material/FormLabel';
import NotificationsIcon from '@mui/icons-material/Notifications';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Badge from '@mui/material/Badge';
import Select from '@mui/material/Select';
import HomeIcon from '@mui/icons-material/Home';
import StoreIcon from '@mui/icons-material/Store';
//import { Scrollbars } from "react-custom-scrollbars-2";
import { Scrollbars } from "react-custom-scrollbars-2";
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import Items from "./Items";
import DeleteIcon from '@mui/icons-material/Delete';
import Alert from '@mui/material/Alert';
import AlertTitle from '@mui/material/AlertTitle';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import "../stylesheet/cart.css";
// Components
import Slider from './Slider';

import './Slider/slider.css';
import { BoxContainer } from '../accountBox/common';
import Slide from '@mui/material/Slide';
const SliderProps = {
  zoomFactor: 8, // How much the image should zoom on hover in percent
  slideMargin: 10, // Margin on each side of slides
  maxVisibleSlides: 6,
  pageTransition: 500 // Transition when flipping pages
};

const theme = createTheme({
  palette: {
    secondary: {
      // This is green.A700 as hex.
      main: '#264653',
    },
  },
  typography: {
    h5: {
      fontWeight: 450 // or 'bold'
    },
    h4:{
      fontSize:'22px',
      fontWeight:420
    }
  }
});
const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function Cart({Id,setiscart}) {
  console.log(Id);
  const [open, setOpen] = React.useState(false);
  const [message,setmessage]=useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const shoot=()=>{
    setmessage(true);
}
  const {
    state: { cart },
    dispatch,
  } = CartState();
  console.log(cart);
  const [total, setTotal] = useState();
  const handleClickOpen = () => {
    setOpen(true);
   
  };
useEffect(() => {
  setTotal(
    cart.reduce((acc, curr) => acc + Number(curr.price) * curr.qty, 0)
  );
}, [cart]);

let history = useHistory();
const checkout = async (body) =>{
  console.log(body)
   await fetch(`https://devhomoeoback.herokuapp.com/user/${Id}/cart/checkout/confirm`, {
        method:"POST",
        headers:{ "Content-Type": "application/json" },
        body: JSON.stringify(body)
    });
   dispatch({
    type: "CLEAR_CART",
    payload: cart,
  })
   fetch(`https://devhomoeoback.herokuapp.com/user/${Id}/cart/clearCart`, {
    method: 'PUT',
   
});
  // const cart2=cart;
  // console.log(cart2)
//   const response=await fetch(`https://devhomoeoback.herokuapp.com/user/${Id}/cart/checkout/confirm`, {
//     method:"POST",
//     headers:{ "Content-Type": "application/json" },
//     body: JSON.stringify(body)
// });
setOpen(false);
  history.push(`/thank/${Id}`)
  
  
 
console.log("checking")


 
}
const cartclear =async () =>{
  dispatch({
    type: "CLEAR_CART",
    payload: cart,
  })
  await fetch(`https://devhomoeoback.herokuapp.com/user/${Id}/cart/clearCart`, {
    method: 'PUT',
   
});
}
    return (
      <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1}} >
      <AppBar position="static" color="secondary">
        <Toolbar>
        
        <IconButton
          size="large"
          aria-label="show 3 items"
          color="inherit"
        >
          <Badge badgeContent={cart.length} color="error">
          <ShoppingCartIcon />
          
          </Badge>
        </IconButton>
          <Typography variant="h5" component="div" sx={{ flexGrow: 1 }} >
          Cart
          </Typography>
          <Box>
          <Link to={"/home"} className="link"> <HomeIcon fontSize="large" /> </Link>
          <Link to={"/all_products_user/"+Id} className="link"> <StoreIcon fontSize="large"/> </Link>
        
          <Link to="/logout" className="link"> <Button variant="outlined" color="inherit">Logout</Button></Link>
          </Box>
          </Toolbar>
          </AppBar>
         
          <section className="main-cart-section">
         
          <p className="total-items">
            you have <span className="total-items-count">{cart.length} </span> {cart.length>1?"items ":"item "} 
            in shopping cart
          </p>
          {message===true?
            <Alert variant="filled" severity="error" id="acenter">
            <AlertTitle>Your cart is empty. Please add some products.</AlertTitle>
            
          </Alert>
                      :null} 
          <div className="cart-items">
            <div className="cart-items-container">
            <hr />
              <Scrollbars>
                {cart.map((curItem) => {
                  return <Items key={curItem._id} {...curItem} Id={Id} />;
                })}
              </Scrollbars>
            </div>
          </div>
                
          <div id="cartnew" >
          
          <h3>
            Cart Total : <span>₹{total}.00</span>
          </h3>
          <Button sx={{ mr: 2}} color="error" variant="outlined" onClick={cartclear} startIcon={<DeleteIcon />}>
    Clear Cart
  </Button>
  {total===0?
    <Button color="success" variant="contained" onClick={shoot} endIcon={<CheckCircleIcon />}>
    Checkout
  </Button>:
  <Button color="success" variant="contained" onClick={handleClickOpen} endIcon={<CheckCircleIcon />}>
      Checkout
    </Button>
}
      
    </div>
    
        </section>
        <Dialog
        open={open}
        onClose={handleClose}
        TransitionComponent={Transition}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
      
        <DialogTitle id="alert-dialog-title">
        {"Do you want to confirm this order ?"}
        </DialogTitle>
        <DialogContent>
        <DialogContentText>
        {"Total Amount ₹"+total+".00"}
      </DialogContentText>
      </DialogContent>
        <DialogActions>
            <Button onClick={handleClose}>Discard</Button>
            <Button onClick={() =>checkout(cart)} autoFocus>
              Confirm
            </Button>
          </DialogActions>
        </Dialog>
        
      
        
        
      </Box>
      </ThemeProvider>
    );
  }

export default Cart
